import React from "react";
// import Logo from "assets/logo/logo.png";

// import CssBaseline from "@mui/material/CssBaseline";
import Container from "@mui/material/Container";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import { List, MenuItem } from "@mui/material";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import SwipeableDrawer from "@mui/material/SwipeableDrawer";
import Slide from "@mui/material/Slide";
import { useNavigate } from "react-router-dom";
import logo from "../assets/images/logo-white.png";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";

// eslint-disable-next-line no-unused-vars
const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function Navigation({ children }) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const navigate = useNavigate();
  const [openDrawer, setOpenDrawer] = React.useState(false);

  const pages = [
    { titre: "Home", lien: "" },
    { titre: "A propos de moi", lien: "a-propos" },
    { titre: "Mon atelier", lien: "atelier" },
    { titre: "Contact", lien: "contact" },
  ];

  const toggleDrawer = (newOpenDrawer) => () => {
    setOpenDrawer(newOpenDrawer);
  };

  // const useStyles = makeStyles({
  //   logo: {
  //     width: 135,
  //     height: 43.54,
  //     display: "flex",
  //     position: "absolute",
  //     // height: "101.82292175292969px",
  //     // width: "229.27728271484375px",
  //     borderRadius: "0px",
  //     left: "42.69%",
  //     right: "41.37%",
  //     top: "-0.75%",
  //     bottom: "30.21%",

  //   },
  // });

  console.log(logo);

  const container =
    pages.window !== undefined ? () => pages.window().document.body : undefined;

  return (
    <div>
      <AppBar
        sx={{
          backgroundColor: "transparent",
          boxShadow: "none",
          with: "100%",
          left: "0px",
          top: "24px",
          position: "relative",
          width: "100%",
          height: "96px",
        }}
      >
        <Box>
          <Toolbar disableGutters sx={{ display: { xs: "flex", md: "block" } }}>
            <Grid justify={"space-between"} container>
              <Grid xs={1} item>
                <img
                  // className={classes.logo}
                  sx={{
                    display: "flex",
                    position: "absolute",
                    // height: "101.82292175292969px",
                    // width: "229.27728271484375px",
                    width: 135,
                    height: 43.54,
                    borderRadius: "0px",
                    left: "42.69%",
                    right: "41.37%",
                    top: "-0.75%",
                    bottom: "30.21%",

                  }}
                  src={logo}
                  alt="logo"
                />
              </Grid>
            </Grid>
            <Box sx={{ display: { xs: "none", md: "block" } }}>
              <List
                sx={{
                  flexGrow: 1,
                  display: "flex",
                  justifyContent: "space-around",
                }}
              >
                {pages.map((page) => (
                  <MenuItem
                    key={page.titre}
                    onClick={() => navigate({ pathname: `/${page.lien}` })}
                  >
                    <Typography
                      sx={{
                        position: "absolute",
                        left: "8.19%",
                        right: "87.85%",
                        top: "26.04%;",
                        bottom: "41.67%",
                        fontFamily: "Libre Baskerville",
                        fontStyle: "normal",
                        fontWeight: 400,
                        fontSize: "14px",
                        lineHeight: "220%",
                        display: "flex",
                        alignItems: "center",
                        textAlign: "center",
                        color: "#000",
                      }}
                    >
                      {page.titre}
                    </Typography>
                  </MenuItem>
                ))}
              </List>
            </Box>
            {/* Menu responsive */}
            <Box
              sx={{
                flexGrow: 1,
                display: { xs: "flex", md: "none" },
                flexDirection: "row-reverse",
              }}
            >
              <IconButton
                size="large"
                onClick={toggleDrawer(true)}
                color="inherit"
              >
                <MenuIcon />
              </IconButton>
              <SwipeableDrawer
                open={openDrawer}
                container={container}
                anchor="bottom"
                onClose={toggleDrawer(false)}
                onOpen={toggleDrawer(true)}
                disableSwipeToOpen={false}
                ModalProps={{ keepMounted: true }}
              >
                <Box sx={{ listStyleType: "none" }}>
                  {pages.map((page, index) => (
                    <MenuItem
                      key={index}
                      onClick={() => navigate({ pathname: `/${page.lien}` })}
                      sx={{ justifyContent: "center" }}
                    >
                      <Typography>{page.titre}</Typography>
                    </MenuItem>
                  ))}
                </Box>
              </SwipeableDrawer>
            </Box>
          </Toolbar>
        </Box>
      </AppBar>

      <Container component="main" disableGutters maxWidth="100%">
        {children}
      </Container>
    </div>
  );
}
